    private void btnDeletarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeletarActionPerformed
        Object[] options = {"Sim", "Não"};
        
        int n = JOptionPane.showOptionDialog(
                null,
                "Tem certeza que deseja deletar?",
                "Confirmar ação",
                JOptionPane.YES_NO_CANCEL_OPTION,
                JOptionPane.QUESTION_MESSAGE,
                null,
                options,
                options[1]
        );
        
        // Confirmou
        if (n == 0) {
            for (int i = 0; i < tblFornecedor.getRowCount(); i++) {
                Boolean chkDel = Boolean.valueOf(
                        tblFornecedor.getValueAt(i, 7).toString()
                );
                
                if (chkDel) {
                    int fornecedorId = Integer.parseInt(
                            tblFornecedor.getValueAt(i, 0).toString()
                    );
                    
                    FornecedorControle fornecedorC = new FornecedorControle();
                    
                    fornecedorC.excluirFornecedorMultiplo(fornecedorId);
                }
            }
            JOptionPane.showMessageDialog(null, "Dados deletados com sucesso");
                
            iniciaTabela();
        }
    }//GEN-LAST:event_btnDeletarActionPerformed

