    private ArrayList<FornecedorModelo> iniciaTabela() {
        int i;
        DefaultTableModel model=(DefaultTableModel)tblFornecedor.getModel();
        // atribui 0 linhas à coluna
        model.setNumRows(0);
        // atribui um tamanho fixo a coluna codigo
        tblFornecedor.getColumnModel().getColumn(0).setPreferredWidth(2);
        ArrayList<FornecedorModelo> listaFornecedores = new ArrayList<FornecedorModelo>();
        listaFornecedores = new FornecedorControle().listarFornecedor();
        //adiciona alunos as tabelas
        for (i=0;i<=listaFornecedores.size()-1;i++){
            model.addRow(
                    new Object[]{
                        listaFornecedores.get(i).getCodFornecedor(),
                        listaFornecedores.get(i).getNomeFantasia(),
                        listaFornecedores.get(i).getRazaoSocial(),
                        listaFornecedores.get(i).getEndereco(),
                        listaFornecedores.get(i).getEmail(),
                        listaFornecedores.get(i).getTelefone(),
                        listaFornecedores.get(i).getCnpj(),
                        Boolean.FALSE
                    }
            );
        }
        return listaFornecedores;
    }
